let topic =  `
SELECT
    time,
    AVG(time_led_on_ms) AS time_led_on_ms,
    SUM(led_broken)  AS lb,
    AVG(led_intensity) AS li,
    AVG(power_Wh) AS pow,
    AVG(light_intensity) AS lii,
    AVG(temp) AS t,
    AVG(humidity) AS hum
FROM Data
WHERE
	${gid_cond}
	${id_cond}
    (julianday('now') - julianday(time))*24*60 < 120
GROUP BY time
ORDER BY time;
`




